document.addEventListener("DOMContentLoaded",function(e){const t=6,o=document.querySelector("#hr"),r=document.querySelector("#min"),n=document.querySelector("#sec");setInterval(()=>{let e=new Date,s=30*e.getHours(),i=e.getMinutes()*t,l=e.getSeconds()*t;o.style.transform=`rotateZ(${s+i/12}deg)`,r.style.transform=`rotateZ(${i}deg)`,n.style.transform=`rotateZ(${l}deg)`});var s=document.querySelector(".timezone");s.innerHTML=Intl.DateTimeFormat().resolvedOptions().timeZone;var i=document.querySelector(".portfolio-button");setTimeout(()=>{i.style.visibility="visible",i.style.opacity=1},2500)});